mapper.delete:
DELETE
FROM notice
WHERE NOTC_NO = #{notcNo}
  and REGR_ID = #{regrId}

mapper.update:
UPDATE notice
SET TITLE    = #{title}
  , CONTENT  = #{content}
  , UPD_DTTM = now()
WHERE NOTC_NO = #{notcNo}
  and REGR_ID = #{regrId}

UPDATE notice
SET VIEW_CNT = VIEW_CNT + 1
WHERE NOTC_NO = #{notcNo}
